import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './Spinner';
export class News extends Component {

    constructor()
    {
        super();
        this.state = {
            articles : [],
            loading : false,
            page : 1
        }
    }

    async componentDidMount(){
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=3d659af675ac480ca332a8b841c691da&page=${this.state.page-1}&pageSize=${this.props.pagesize}`;
        this.setState({loading: true});
        let data= await fetch(url);
        let parsedData = await data.json();
        this.setState({
            articles : parsedData.articles,
            totalResults: parsedData.totalResults,
            loading:false
        });
    }

    handlePrevclick= async()=>{
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=3d659af675ac480ca332a8b841c691da&page=${this.state.page-1}&pageSize=${this.props.pagesize}`;
        this.setState({loading: true});
        let data= await fetch(url);
        let parsedData = await data.json();
                
        this.setState({
            articles : parsedData.articles,
            page: this.state.page-1,
            loading: false
        });
    };

    handleNextclick= async()=>{
        console.log("clicked")
        if(!this.state.page+1 > Math.ceil(this.state.totalResults/this.props.pagesize))
        {
        let url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=3d659af675ac480ca332a8b841c691da&page=${this.state.page+1}&pageSize=${this.props.pagesize}`;
        this.setState({loading: true});
        let data= await fetch(url);
        let parsedData = await data.json();

        this.setState({
            articles : parsedData.articles,
            page: this.state.page+1,
            loading:false
        });
        }
    };


  render(){
    return (
      <div className='container my-3'>
      <h2 className='text-center'>Top Headlines</h2>
      {this.state.loading && <Spinner />}
        <div className="row">
        {!this.state.loading && this.state.articles.map((element)=>{
            return(
                <div className="col-md-4" key={element.url} >
                <NewsItem title={element.title?element.title.slice(0,20):""} description={element.description?element.description.slice(0, 100):""} ImageUrl={element.urlToImage} newsUrl={element.url}/>
                </div>
            )
        })}     
        </div>
        <div className="container d-flex justify-content-between">
        <button disabled={this.state.page<=1} type="button" className="btn btn-dark " onClick={this.handlePrevclick}>Prev</button>
        <button disabled={!this.state.page+1 > Math.ceil(this.state.totalResults/this.props.pagesize)} type="button" className="btn btn-dark " onClick={this.handleNextclick}>Next</button>
        </div>
      </div>
    )
  }
}

export default News
