import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {
    articles = [
        {
         "source": {
         "id": null,
         "name": "The Guardian"
         },
         "author": "Sian Cain",
         "title": "Blade Runner 2099: ‘provocative’ TV sequel greenlit by Amazon",
         "description": "With Ridley Scott as executive producer, Amazon promises the TV show ‘will uphold the intellect, themes and spirit of its film predecessors’<ul><li>Get our weekend culture and lifestyle email</li></ul>A Blade Runner TV show is in the works at Amazon Studios, …",
         "url": "https://amp.theguardian.com/tv-and-radio/2022/sep/16/blade-runner-2099-provocative-tv-sequel-greenlit-by-amazon",
         "urlToImage": "https://i.guim.co.uk/img/media/3b659d294ebc3927cc71e406dcd3838bdb833174/233_421_1996_1198/master/1996.jpg?width=1200&height=630&quality=85&auto=format&fit=crop&overlay-align=bottom%2Cleft&overlay-width=100p&overlay-base64=L2ltZy9zdGF0aWMvb3ZlcmxheXMvdGctZGVmYXVsdC5wbmc&enable=upscale&s=1f08ea0070397bb58878d281d7978b30",
         "publishedAt": "2022-09-16T01:05:41Z",
         "content": "A Blade Runner TV show is in the works at Amazon Studios, the media giant has confirmed, with the original films director Ridley Scott on board as executive producer.\r\nBlade Runner 2099 will be a lim… [+2186 chars]"
         },
        {
         "source": {
         "id": null,
         "name": "MacRumors"
         },
         "author": "Sami Fathi",
         "title": "Apple Confirms iOS 16 Bug Impacting New Device Activation as iPhone 14 Orders Begin to Arrive",
         "description": "Apple has confirmed that customers activating new iPhones may encounter an issue where device activation during initial setup doesn't go through on Wi-Fi, saying the problem is \"under investigation.\"\n\n\n\n\n\nIn a memo seen by MacRumors, Apple says that \"there is…",
         "url": "https://www.macrumors.com/2022/09/15/apple-confirms-ios-16-device-activation-bug/",
         "urlToImage": "https://images.macrumors.com/t/RaBq1kQm57HqYgjTIXI3iF3ucKU=/2500x/article-new/2022/09/iPhone-14-Pros-in-Hand-Black-Background-Feature.jpg",
         "publishedAt": "2022-09-16T02:27:38Z",
         "content": "Apple has confirmed that customers activating new iPhones may encounter an issue where device activation during initial setup doesn't go through on Wi-Fi, saying the problem is \"under investigation.\"… [+1214 chars]"
         },
        {
         "source": {
         "id": null,
         "name": "MacRumors"
         },
         "author": "Joe Rossignol",
         "title": "iOS 16.1 Will Bring Live Sports Scores to Your iPhone's Lock Screen",
         "description": "Apple recently announced that iOS 16.1 will enable a new Live Activities feature that allows iPhone users to stay on top of things that are happening in real time, such as a sports game or a food delivery order, right from the Lock Screen.\n\n\n\n\n\nIn the first i…",
         "url": "https://www.macrumors.com/2022/09/15/ios-16-1-live-activities-for-sports/",
         "urlToImage": "https://images.macrumors.com/t/SIzLgA6NM6jCEis8IACMIC0pZBA=/1600x/article-new/2022/09/iOS-16-Live-Activities-Sports-MLB.jpeg",
         "publishedAt": "2022-09-16T01:22:32Z",
         "content": "Apple recently announced that iOS 16.1 will enable a new Live Activities feature that allows iPhone users to stay on top of things that are happening in real time, such as a sports game or a food del… [+1670 chars]"
         },
        {
         "source": {
         "id": null,
         "name": "MacRumors"
         },
         "author": "Joe Rossignol",
         "title": "Apple Reveals 8 Scenarios Where iPhone 14 Pro's Always-On Display Turns Off",
         "description": "Apple today published a new support document with additional details about the new always-on display mode on the iPhone 14 Pro and iPhone 14 Pro Max. Notably, the document lists eight scenarios where the always-on display temporarily turns off.\n\n\n\n\n\nTo save b…",
         "url": "https://www.macrumors.com/2022/09/15/apple-details-iphone-14-pro-always-on-display/",
         "urlToImage": "https://images.macrumors.com/t/o8KFRSW03fQ-ZjqhZNgs0vKKisk=/1600x/article-new/2022/09/iphone-14-pro-always-on-display.jpeg",
         "publishedAt": "2022-09-16T02:48:33Z",
         "content": "Apple today published a new support document with additional details about the new always-on display mode on the iPhone 14 Pro and iPhone 14 Pro Max. Notably, the document lists eight scenarios where… [+1584 chars]"
         }
         ]
    constructor()
    {
        super();
        this.state = {
            articles : this.articles,
            loading : false,
            page : 1
        }
    }


    handlePrevclick= async()=>{

    }

    handleNextclick= async()=>{
        let url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=3d659af675ac480ca332a8b841c691da";
                let data= await fetch(url);
                let parsedData = await data.json();
                
                this.setState({articles : parsedData.articles});
    }


  render(){
    return (
      <div className='container my-3'>
      <h2>Top Headlines</h2>
        <div className="row">
        {this.state.articles.map((element)=>{
            return(
                <div className="col-md-4" key={element.url} >
                <NewsItem title={element.title?element.title.slice(0,20):""} description={element.description?element.description.slice(0, 100):""} ImageUrl={element.urlToImage} newsUrl={element.url}/>
                </div>
            )
        })}     
        </div>
        <div className="container d-flex justify-content-between">
        <button disabled={this.state.page<=1} type="button" class="btn btn-dark " onClick={this.handlePrevclick}>Prev</button>
        <button type="button" class="btn btn-dark " onClick={this.handleNextclick}>Next</button>
        </div>
      </div>
    )
  }
}

export default News
