import React, { Component } from 'react'

export class NewsItem extends Component {
  render() {
    let {title, description} = this.props;
    return (
      <div>
        <div className="card" style={{width: '18rem'}}>
        <img src="https://i.guim.co.uk/img/media/3b659d294ebc3927cc71e406dcd3838bdb833174/233_421_1996_1198/master/1996.jpg?width=1200&height=630&quality=85&auto=format&fit=crop&overlay-align=bottom%2Cleft&overlay-width=100p&overlay-base64=L2ltZy9zdGF0aWMvb3ZlcmxheXMvdGctZGVmYXVsdC5wbmc&enable=upscale&s=1f08ea0070397bb58878d281d7978b30" className="card-img-top" alt="..." />
        <div className="card-body">
            <h5 className="card-title">{title}</h5>
            <p className="card-text">{description}</p>
            <a href="/" className="btn btn-primary">Read More</a>
        </div>
        </div>
      </div>
    )
  }
}

export default NewsItem
